import React, { useState, useContext, useEffect } from 'react';
import { FiBookmark, FiMoon, FiSun, FiPlus, FiLogOut } from 'react-icons/fi';

import Alert from '../../components/Alert';
import NoteItem from '../../components/NoteItem';
import NoteModal from '../../components/NoteModal';

import AppContext, { AppContextType } from '../../contexts/AppContext';
import UserContext, { UserContextType } from '../../contexts/UserContext';
import NoteContext, { NoteContextType } from '../../contexts/NoteContext';
import StatusContext, { StatusContextType } from '../../contexts/StatusContext';

import { NoteType } from '../../utils/types';
import './styles.css';

const Home: React.FC = () => {

	const {
		isDarkMode,
		changeTheme
	} = useContext<AppContextType>(AppContext);
	const {
		notesArray,
		noteContextError,
		getNotes,
		deleteNote,
		createNoteContextError,
		clearNoteContextError
	} = useContext<NoteContextType>(NoteContext);
	const {
		user,
		signout
	} = useContext<UserContextType>(UserContext);
	const {
		statusArray,
		statusContextError,
		getStatus,
		createStatusContextError,
		clearStatusContextError
	} = useContext<StatusContextType>(StatusContext);

	const [ showNoteModal, setShowNoteModal ] = useState<boolean>(false);

	const [ selectedStatus, setSelectedStatus ] = useState<string>('none');
	const [ selectedNote, setSelectedNote ] = useState<NoteType | any>(null);

	const handleEdit = (note: NoteType) => {
		setSelectedNote(note);
		setShowNoteModal(true);
	}

	const handleCloseNoteModal = () => {
		setShowNoteModal(false);
		setSelectedStatus('');
		if (selectedNote) setSelectedNote(null);
	}

	useEffect(() => {
		(async () => {
			try {
				await getStatus();
			} catch (err) {
				createStatusContextError(err);
			}
		})();
	}, []);

	useEffect(() => {
		(async () => {
			try {
				await getNotes(user.id_user, selectedStatus);
			} catch (err) {
				createNoteContextError(err);
			}
		})();
	}, [selectedStatus]);

	return (
		<div className="Home">

			{ statusContextError != '' && <Alert message={ statusContextError } onClose={ clearStatusContextError } /> }
			{ noteContextError != '' && <Alert message={ noteContextError } onClose={ clearNoteContextError } /> }

			{ user != null && showNoteModal && <NoteModal onClose={ handleCloseNoteModal } note={ selectedNote } /> }

			<div className="wallpaper"></div>

			<div className="container">
				<h2>Via Notes</h2>

				<div className="content">
					<div className="header">
						<div className="user-box">
							<img src="data:image/jpeg;base64," />
							
							<div className="user-info">
								<h3>{ user.name }</h3>
								<h4>{ user.email }</h4>
							</div>
						</div>

						<div className="buttons">
							<button className="btn-circle" title="Sair" onClick={ signout }>
								<FiLogOut size={ 24 } className="icon" />
							</button>
							
							<button className="btn-circle margin" title="Nova Nota" onClick={ changeTheme }>
								{ isDarkMode ? <FiSun size={ 24 } className="icon" /> : <FiMoon size={ 24 } className="icon" /> }
							</button>

							<button className="btn-circle" title="Nova Nota" onClick={ () => setShowNoteModal(true) }>
								<FiPlus size={ 24 } className="icon" />
							</button>
						</div>
					</div>	

					<div className="sub-header">
						<p>
							{
								notesArray.length == 0 ? 
								'Sem notas' : 
								notesArray.length == 1 ? 
								`1 Nota` : 
								`${notesArray.length} Notas`
							}
						</p>

						<div className="select-box">
							<FiBookmark size={ 20 } className="icon" />

							<select value={ selectedStatus } onChange={ (ev) => setSelectedStatus(ev.target.value) }>
								<option value="none">Todas</option>
								{
									statusArray.length > 0 && statusArray.map((status, index) => (
										<option key={ index.toString() } value={ status.id_status }>
											{ status.name }
										</option>
									))
								}
							</select>
						</div>
					</div>

					<div className="list">
						{
							notesArray.length > 0 ?
								notesArray.map((note, index) => (
									<NoteItem
										key={ index.toString() }
										note={ note }
										onEdit={ () => handleEdit(note) }
										onDelete={ () => deleteNote(note.id_note) }
									/>
								))
							: (
								<p className="no-notes">
									Sem anotações! Tente criar uma clicando no  <b>ícone de mais</b>.
								</p>
							)
						}
					</div>
				</div>
			</div>

			<div className="footer">
				<p>
					<em>&copy; Created by <b>Névio Costa Magagnin</b> - { new Date().getFullYear() }</em>
				</p>
			</div>

		</div>
	);

}

export default Home;
